import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
const axios = require("axios");

class App extends Component {
  async componentDidMount() {
    var socket = new WebSocket("wss://stream.binance.com:9443/ws/bnbusdt@trade");

    socket.onmessage =  (event) => {
      const _price = JSON.parse(event.data).p;
      const price = Number(_price).toFixed(2);
      axios.get("https://api.bscscan.com/api?module=account&action=tokenbalance&contractaddress=0x851f7a700c5d67db59612b871338a85526752c25&address=0x793FE5a628a79Ae3E619ACbCde1CBaf8053beFc5&tag=latest&apikey=YourApiKeyToken").then((data) => {
        console.log(data);
      })
      console.log(price);
    }
  }
  render() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
}

export default App;
